# Change these if needed
$GAMEDIR = "C:\Program Files (x86)\Roblox\Versions\";

# Shouldn't need to change these
$GAMEDIR = (Get-ChildItem "$($GAMEDIR)version-*" | Select-Object -ExpandProperty FullName -first 1)
$SOUND_LOC = "${GAMEDIR}/content/sounds/ouch.ogg"

if (!$GAMEDIR) {
    Write-Host "Couldn't find Roblox game directory"
    exit 1  
}

# Backup the old file
$HASH_RES = Get-FileHash -Algorithm MD5 $SOUND_LOC | Select-Object -ExpandProperty Hash

if ($HASH_RES -ne "E1E7C7968C30018FD0A5C15681D91897") {
}

# Download oof sound file
$wc = New-Object System.Net.WebClient
$wc.DownloadFile("https://github.com/wxllow/00f/raw/main/sounds/uuhhh.ogg", $SOUND_LOC)

# Play the oof sound
Try {
    mpv $SOUND_LOC &
}
Catch {} 

